    val split = input.split('\n')
    val max = split.maxBy { it.length }?.length ?: 0
    val diagram = split.map { it.padEnd(max, ' ') }
    println(Day19a.solveFor(diagram))
}

object Day19a {
    fun solveFor(diagram: List<String>): Pair<String, Int> {
        val start = diagram[0].indexOf('|')
        var coord = start to 0
        var direction: Direction = S
        var sequence = ""
        var steps = 0

        while (direction.canAdvanceFrom(coord, diagram)) {
            if (diagram at coord in 'A'..'Z') {
                sequence += diagram at coord
            }
            direction = direction.directionFrom(coord, diagram)
            coord = direction.advanceFrom(coord, diagram)
            steps++
        }
        return sequence to steps
    }
}

private infix fun List<CharSequence>.at(coord: Coords): Char {
    return this[coord.y][coord.x]
}

val letters = 'A'..'Z'
val directionChars = listOf('|', '-', *letters.toArray())

sealed class Direction {
    abstract fun canAdvanceFrom(coord: Coords, diagram: List<String>): Boolean
    abstract fun advanceFrom(coord: Coords, diagram: List<String>): Coords
    abstract fun directionFrom(coord: Coords, diagram: List<String>): Direction
}

object N : Direction() {
    override fun canAdvanceFrom(coord: Coords, diagram: List<String>): Boolean = when (diagram at coord) {
        in directionChars -> coord.y > 0
        '+' -> coord.x in 0 until diagram[coord.y].length
        else -> false
    }

    override fun advanceFrom(coord: Coords, diagram: List<String>): Coords = coord.x to coord.y - 1

    override fun directionFrom(coord: Coords, diagram: List<String>): Direction = when (diagram at coord) {
        in directionChars -> N
        '+' -> if (coord.x > 0 && diagram at coord.west() in directionChars) {
            W
        } else {
            E
        }
        else -> throw IllegalStateException("${diagram at coord} at $coord not a valid state")
    }
}

object W : Direction() {
    override fun canAdvanceFrom(coord: Coords, diagram: List<String>): Boolean = when (diagram at coord) {
        in directionChars -> coord.x > 0
        '+' -> coord.y in 0 until diagram.size
        else -> false
    }

    override fun advanceFrom(coord: Coords, diagram: List<String>): Coords = coord.x - 1 to coord.y

    override fun directionFrom(coord: Coords, diagram: List<String>): Direction = when (diagram at coord) {
        in directionChars -> W
        '+' -> if (coord.y > 0 && diagram at coord.north() in directionChars) {
            N
        } else {
            S
        }
        else -> throw IllegalStateException("${diagram at coord} at $coord not a valid state")
    }
}

object E : Direction() {
    override fun canAdvanceFrom(coord: Coords, diagram: List<String>): Boolean = when (diagram at coord) {
        in directionChars -> coord.x < diagram[coord.y].length - 1
        '+' -> coord.y in 0 until diagram.size
        else -> false
    }

    override fun advanceFrom(coord: Coords, diagram: List<String>): Coords = coord.x + 1 to coord.y

    override fun directionFrom(coord: Coords, diagram: List<String>): Direction = when (diagram at coord) {
        in directionChars -> E
        '+' -> if (coord.y > 0 && diagram at coord.north() in directionChars) {
            N
        } else {
            S
        }
        else -> throw IllegalStateException("${diagram at coord} at $coord not a valid state")
    }
}

object S : Direction() {
    override fun canAdvanceFrom(coord: Coords, diagram: List<String>): Boolean = when (diagram at coord) {
        in directionChars -> coord.y < diagram.size - 1
        '+' -> coord.x in 0 until diagram[coord.y].length
        else -> false
    }

    override fun advanceFrom(coord: Coords, diagram: List<String>): Coords = coord.x to coord.y + 1

    override fun directionFrom(coord: Coords, diagram: List<String>): Direction = when (diagram at coord) {
        in directionChars -> S
        '+' -> if (coord.x > 0 && diagram at coord.west() in directionChars) {
            W
        } else {
            E
        }
        else -> throw IllegalStateException("${diagram at coord} at $coord not a valid state")
    }
}

data class Coords(val x: Int, val y: Int)
fun Coords.north() = x to y - 1
fun Coords.south() = x to y + 1
fun Coords.west() = x - 1 to y
fun Coords.east() = x + 1 to y

infix fun Int.to(other: Int) = Coords(this, other)

fun CharRange.toArray(): Array<Char> = this.joinToString("").toCharArray().toTypedArray()